import React from 'react';
import { Pressable, StyleSheet, Text, View } from 'react-native';
import { useState } from "react";
import happyWolf from './assets/placeholder-happywolf';
import angryWolf from './assets/placeholder-angrywolf.png';

export default function WildPet(){
    const [visible, setVisible] = useState(true);

    function toggleVisibility(){
        return setVisible(!visible);
    };

    const [happyStatus, setHappyStatus] = useState(80);
    const [fullStatus, setFullStatus] = useState(80);
    const [entertainedStatus, setEntertainedStatus] = useState(80);
    const [petMessage, setPetMessage] = useState("I'm your pet!");
    const [mood, setMood] = useState(happyWolf);

    var myPet = {
        petName: 'Fido',
        //additional details to be added.
    };

    function updateMessage () {
        if(fullStatus > 70 && happyStatus > 70 && entertainedStatus > 70){
            setPetMessage("I am loved!");
        }else if(fullStatus <= 70 || happyStatus <= 70 || entertainedStatus <= 70){
            setPetMessage("I need you.");
        };
        //addition message outputs to be added.
        return;
    };

    function updateMood () {
        if(fullStatus > 70 && happyStatus > 70 && entertainedStatus > 70){
            setMood(happyWolf);
        }else if(fullStatus <= 70 || happyStatus <= 70 || entertainedStatus <= 70){
            setMood(angryWolf);
        };
        //addition message outputs to be added.
        return;
    };

    function pet () {
        setHappyStatus(happyStatus+10);
        setEntertainedStatus(entertainedStatus+10);
        updateMessage();
        updateMood();
        console.log('Pet:',happyStatus,fullStatus,entertainedStatus);
        return;
    }
    function feed () {
        setFullStatus(fullStatus+10);
        setHappyStatus(happyStatus+10);
        updateMessage();
        updateMood();
        console.log('Feed:',happyStatus,fullStatus,entertainedStatus);
        return;
    };
    function play () {
        setEntertainedStatus(entertainedStatus+10);
        setHappyStatus(happyStatus+10);
        setFullStatus(fullStatus-10);
        updateMessage();
        updateMood();
        console.log('Play:',happyStatus,fullStatus,entertainedStatus);
        return;
    };

    return(
        <View style={styles.body}>
            <Pressable onPress={toggleVisibility}>
                <Text style={styles.h1}>My Wild Pet</Text>
                <Text>Personal task, inspired by a popular digital pet game. </Text>
                <Text style={{fontStyle: 'italic'}}>Click to view.</Text>
            </Pressable>
            <View style={visible ? styles.showPet : styles.hide}>
                <View style={styles.petContainer}>
                    <Text>{myPet.petName}</Text>
                    <Text>{petMessage}</Text>
                    <Text>Happy: {happyStatus} | Full: {fullStatus} | Entertained: {entertainedStatus}</Text>
                </View>
                <View style={styles.buttonRow}>
                    <Pressable style={styles.button} 
                        onPress={pet}>
                            <Text style={styles.buttonLabel}>Pet</Text>
                    </Pressable> 
                    <Pressable style={styles.button} 
                        onPress={feed}>
                            <Text style={styles.buttonLabel}>Feed</Text>
                    </Pressable> 
                    <Pressable style={styles.button} 
                        onPress={play}>
                            <Text style={styles.buttonLabel}>Play</Text>
                    </Pressable> 
                </View> 
            </View>
        </View>
      );
};

const styles = StyleSheet.create({
    body:{
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        height: 450,
        marginTop: 10,
    },
    h1:{
        fontWeight:"bold",
        fontSize: 18,
        textAlign: 'center',
    },
    hide:{
        display: 'none',
        height: 10,
    },
    showPet:{
        //display: 'flex',
        alignItems: 'center',
        height: 'auto',
        width: 300,
    },
    petContainer:{
        height: 150,
        width: 'auto',
    },
    buttonRow:{
        height: 50,
        width: 'auto',
        alignItems: 'center',
    },
    button:{
        borderRadius: 10, 
        height: 25,
        width: 100,
        backgroundColor: 'blue',
        alignContent: 'center',
        justifyContent: 'center',
        margin: 1,
    },
    buttonLabel: {
        color: '#fff',
        fontWeight: 'bold',
        fontSize: 15,
        textAlign: 'center',
    },

});
