import React from 'react';
import { StyleSheet, Text, View } from 'react-native';
import { SafeAreaView } from 'react-native-safe-area-context';
import { StatusBar } from 'expo-status-bar';
import Nav from './Nav';

export default function About({navigation}){

    return(
        <SafeAreaView style={styles.container}>
            <View style={styles.main}>
                <Nav navigation={navigation}/>
                <View style={styles.body}>
                    <Text>Hello, My name is Chrystal. I am currently learning how to develop cross platform apps with React. Follow my discover journey here and find links to various completed projects, as they become available. 
                        The intention is to practice and explore various features for developing cross platform apps with React.</Text>
                </View>
            </View>
            <StatusBar style="auto"></StatusBar>
        </SafeAreaView>
      );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        height: 'auto',
    },
    main:{
        flex: 1, 
        alignItems: 'center', 
        justifyContent: 'center' 
    },
    body:{
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        height: 'auto',
    },
});
