import React from 'react';
import { Pressable, StyleSheet, Text, View } from 'react-native';
import { useState } from "react";

export default function WildPet(){
    const [visible, setVisible] = useState(true);

    function toggleVisibility(){
        return setVisible(!visible);
    };

    const [petStatus, setPetStatus] = useState({
        happy: 80,
        full: 80,
        entertained: 80,
    })

    var myPet = {
        petName: 'Fido',
        /*changeName: function (name) {
            this.petName = name;
            return;
        },*/
        petMessage: "I'm your pet!",
        //happy: 80,
        //full: 80,
        //entertained: 80,
    };

    function updateMessage () {
        myPet.petMessage = "I'm ";
        if(myPet.full > 50 && myPet.happy > 50 && myPet.entertained > 50){
            myPet.petMessage = "Content! "
        }
        if(myPet.full <= 50){
            myPet.petMessage =+ "hungry, "
        }
        if(myPet.happy <= 50){
            myPet.petMessage =+ "sad, "
        }
        if(myPet.full <= 50){
            myPet.petMessage =+ "bored, "
        };
        myPet.petMessage =+ "Love me more."
        return;
    };
    function pet () {
        setPetStatus(happy+10);
        setPetStatus(entertained+10);
        updateMessage();
        return petStatus;
    }
    function feed () {
        setPetStatus.full =+ 10;
        setPetStatus.happy =+ 10;
        updateMessage();
        return petStatus;
    };
    function play () {
        setPetStatus.entertained =+ 10;
        setPetStatus.happy =+ 10;
        setPetStatus.full =- 10;
        updateMessage();
        return petStatus;
    };

    return(
        <View style={styles.body}>
            <Pressable onPress={toggleVisibility}>
                <Text style={styles.h1}>My Wild Pet</Text>
                <Text>Personal task, inspired by a popular digital pet game. </Text>
                <Text style={{fontStyle: 'italic'}}>Click to view.</Text>
            </Pressable>
            <View style={visible ? styles.showPet : styles.hide}>
                <View style={styles.petContainer}>
                    <Text>{myPet.petName}</Text>
                    <Text>{myPet.petMessage}</Text>
                </View>
                <View style={styles.buttonRow}>
                    <Pressable style={styles.button} 
                        onPress={pet & console.log('Pet:',petStatus)}>
                            <Text style={styles.buttonLabel}>Pet</Text>
                    </Pressable> 
                    <Pressable style={styles.button} 
                        onPress={feed & console.log('Feed:',petStatus)}>
                            <Text style={styles.buttonLabel}>Feed</Text>
                    </Pressable> 
                    <Pressable style={styles.button} 
                        onPress={play & console.log('Play:',petStatus)}>
                            <Text style={styles.buttonLabel}>Play</Text>
                    </Pressable> 
                </View> 
            </View>
        </View>
      );
};

const styles = StyleSheet.create({
    body:{
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        height: 450,
        marginTop: 10,
    },
    h1:{
        fontWeight:"bold",
        fontSize: 18,
        textAlign: 'center',
    },
    hide:{
        display: 'none',
        height: 10,
    },
    showPet:{
        //display: 'flex',
        alignItems: 'center',
        height: 'auto',
        width: 300,
    },
    petContainer:{
        height: 150,
        width: 'auto',
    },
    buttonRow:{
        height: 50,
        width: 'auto',
        alignItems: 'center',
    },
    button:{
        borderRadius: 10, 
        height: 25,
        width: 100,
        backgroundColor: 'blue',
        alignContent: 'center',
        justifyContent: 'center',
        margin: 1,
    },
    buttonLabel: {
        color: '#fff',
        fontWeight: 'bold',
        fontSize: 15,
        textAlign: 'center',
    },

});
