import React from 'react';
import { Pressable, StyleSheet, Text, View } from 'react-native';
import { useState } from "react";

export default function WildPet(){
    const [visible, setVisible] = useState(true);

    function toggleVisibility(){
        return setVisible(!visible);
    };

    const [happyStatus, setHappyStatus] = useState(80);
    const [fullStatus, setFullStatus] = useState(80);
    const [entertainedStatus, setEntertainedStatus] = useState(80);
    const [petMessage, setPetMessage] = useState("I'm your pet!");

    var myPet = {
        petName: 'Fido',
        /*changeName: function (name) {
            this.petName = name;
            return;
        },*/
        //petMessage: "I'm your pet!",
    };

    function updateMessage () {
        var message = "I'm ";
        if(myPet.full > 50 && myPet.happy > 50 && myPet.entertained > 50){
            message = "Content! "
        }
        if(myPet.full <= 50){
            message =+ "hungry, "
        }
        if(myPet.happy <= 50){
            message =+ "sad, "
        }
        if(myPet.full <= 50){
            message =+ "bored, "
        };
        message =+ "Love me more."
        setPetMessage(message);
        return petMessage;
    };
    function pet () {
        setHappyStatus(happy+10);
        setEntertainedStatus(entertained+10);
        updateMessage();
        console.log('Pet:',happyStatus,fullStatus,entertainedStatus)
        return petStatus;
    }
    function feed () {
        setPetStatus.full =+ 10;
        setPetStatus.happy =+ 10;
        updateMessage();
        console.log('Feed:',petStatus)
        return petStatus;
    };
    function play () {
        setPetStatus.entertained =+ 10;
        setPetStatus.happy =+ 10;
        setPetStatus.full =- 10;
        updateMessage();
        console.log('Play:',petStatus)
        return petStatus;
    };

    return(
        <View style={styles.body}>
            <Pressable onPress={toggleVisibility}>
                <Text style={styles.h1}>My Wild Pet</Text>
                <Text>Personal task, inspired by a popular digital pet game. </Text>
                <Text style={{fontStyle: 'italic'}}>Click to view.</Text>
            </Pressable>
            <View style={visible ? styles.showPet : styles.hide}>
                <View style={styles.petContainer}>
                    <Text>{myPet.petName}</Text>
                    <Text>{myPet.petMessage}</Text>
                </View>
                <View style={styles.buttonRow}>
                    <Pressable style={styles.button} 
                        onPress={pet}>
                            <Text style={styles.buttonLabel}>Pet</Text>
                    </Pressable> 
                    <Pressable style={styles.button} 
                        onPress={feed}>
                            <Text style={styles.buttonLabel}>Feed</Text>
                    </Pressable> 
                    <Pressable style={styles.button} 
                        onPress={play}>
                            <Text style={styles.buttonLabel}>Play</Text>
                    </Pressable> 
                </View> 
            </View>
        </View>
      );
};

const styles = StyleSheet.create({
    body:{
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        height: 450,
        marginTop: 10,
    },
    h1:{
        fontWeight:"bold",
        fontSize: 18,
        textAlign: 'center',
    },
    hide:{
        display: 'none',
        height: 10,
    },
    showPet:{
        //display: 'flex',
        alignItems: 'center',
        height: 'auto',
        width: 300,
    },
    petContainer:{
        height: 150,
        width: 'auto',
    },
    buttonRow:{
        height: 50,
        width: 'auto',
        alignItems: 'center',
    },
    button:{
        borderRadius: 10, 
        height: 25,
        width: 100,
        backgroundColor: 'blue',
        alignContent: 'center',
        justifyContent: 'center',
        margin: 1,
    },
    buttonLabel: {
        color: '#fff',
        fontWeight: 'bold',
        fontSize: 15,
        textAlign: 'center',
    },

});
