import React from 'react';
import { Pressable, StyleSheet, Text, View } from 'react-native';
import { useState, useRef } from "react";

export default function SimpleCalc(){
    const inputRef = useRef(null); 
    const resultRef = useRef(null); 
    const [result, setResult] = useState(0); 

    function plus(e) { 
      e.preventDefault(); 
      setResult((result) => result + Number(inputRef.current.value));
    }; 

    function minus(e) { 
      e.preventDefault(); 
      setResult((result) => result - Number(inputRef.current.value));
    };

    function times(e) { 
      e.preventDefault(); 
      setResult((result) => result * Number(inputRef.current.value));
    }; 

    function divide(e) { 
      e.preventDefault(); 
      setResult((result) => result / Number(inputRef.current.value));
    };

    function resetInput(e) { 
      e.preventDefault();
      inputRef.current.value = null;
    };

    function resetAll(e) { 
      e.preventDefault();
      setResult(0);
      inputRef.current.value = null;

    }; 
    return(
        <View style={styles.body}>
            <Text style={styles.h1}>Simple Calculator</Text>
            <View>
                <form> 
                <p ref={resultRef}> 
                    {result} 
                </p> 
                <input
                    pattern="[0-9]" 
                    ref={inputRef} 
                    type="number" 
                    placeholder="Type a number" 
                /> 
                <Pressable onPress={plus}><Text>+</Text></Pressable> 
                <Pressable onPress={minus}><Text>-</Text></Pressable> 
                <Pressable onPress={times}><Text>*</Text></Pressable> 
                <Pressable onPress={divide}><Text>/</Text></Pressable> 
                <Pressable onPress={resetInput}><Text>Reset Input</Text></Pressable> 
                <Pressable onPress={resetAll}><Text>Reset All</Text></Pressable> 
                </form> 
            </View>
        </View>
      );
};

const styles = StyleSheet.create({
    body:{
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        height: 'auto',
        marginTop: 10,
    },
    h1:{
        fontWeight:"bold",
        fontSize: 18,
    },
    button:{
        borderRadius: 25, 
        height: 25,
        width: 100,
        backgroundColor: 'orange',
        alignItems: 'center',
        justifyContent: 'center',
        margin: 1,
    },
    buttonLabel: {
        color: '#fff',
        fontWeight: 'bold',
        fontSize: 15,
    },

});
